import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import Container from "@material-ui/core/Container";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import TrafficCard from "../components/TrafficCard";
import CamLocation from "../CameraLocation";
import CheckBoxes from "../components/CheckBoxes";

// const x = {camera_id: "7793",
// image: "https://images.data.gov.sg/api/traffic-images/2021/03/91341dd0-7d15-4758-ba74-9d199d64c61c.jpg",
// image_metadata: {height: 360, width: 640, md5: "021376aa84d5a2d274d6f8d99d7a6b31"},
// location: {latitude: 1.37704704, longitude: 103.92946983},
// timestamp: "2021-03-31T15:09:56+08:00"}

// const l = [{
//      image:
//           "https://images.data.gov.sg/api/traffic-images/2021/03/3e67a31c-cbd0-4a6c-bb24-90b89d00b78c.jpg",
//      camera_id: "7793",
//      location: "",
//      area: "ECP",
// },
// {
//      image:
//           "https://images.data.gov.sg/api/traffic-images/2021/03/545193ed-2468-46f5-ae08-5177992f8c96.jpg",
//      camera_id: "1006",
//      location: "KPE (Tampines)",
//      area: "KPE",
// },]

const TrafficCam = () => {
	const [trafficImg, setTrafficImg] = useState([]);
	// const [trafficFilterImg, useTrafficFilterImg] = useState([]);
	console.log("api", trafficImg);
	console.log("data", CamLocation);

	const area = {};
	for (const element of CamLocation) {
		// if (area.indexOf(element.area) === -1) {
		area[element.area] = false;
		// }
	}
	const v = [];
	console.log(area);
	for (const element of area) {
		if (area[element] === true) {
			v.push(element);
		}
	}

	const [check, setCheck] = useState(area);
	console.log(check);
	const arrBool = Object.values(check);
	console.log(trafficImg);

	useEffect(() => {
		const camImageUrl = "https://api.data.gov.sg/v1/transport/traffic-images";

		axios.get(camImageUrl).then((res) => {
			// console.log(res.data.items[0].cameras);
			setTrafficImg(res.data.items[0].cameras);
		});
	}, []);

	const handleChange = (event) => {
		setCheck({ ...check, [event.target.name]: event.target.checked });
	};

	if (check) {
		for (const index in check) {
			// console.log("lll", Object.values(check));
			// console.log("ttt", Object.keys(check));
		}
	}

	return (
		<div>
			<Container justify="center">
				<Box display="flex" justifyContent="center">
					<Typography variant="h3">Traffic Cameras</Typography>
				</Box>
				<CheckBoxes
					area={area}
					check={check}
					handleChange={handleChange}
					arrBool={arrBool}
				/>

				<Grid container>
					{trafficImg &&
						trafficImg.map(
							(element) =>
								CamLocation.map((item) => {
									// if (check.(item.area))
									if (element.camera_id === item.camera_id)
										return (
											<Grid item key={element.camera_id}>
												<TrafficCard
													img={element.image}
													location={element.location}
													id={element.camera_id}
													road={item.location}
													cluster={item.area}
												/>
											</Grid>
										);
								})
							// if (element.camera_id === CamLocation)
						)}
				</Grid>
			</Container>
		</div>
	);
};

export default TrafficCam;
