						control={
							<Checkbox checked={jason} onChange={handleChange} name="jason" />
						}
						label="Jason Killian"
					/>
					<FormControlLabel
						control={
							<Checkbox
								checked={antoine}
								onChange={handleChange}
								name="antoine"
							/>
						}
						label="Antoine Llorca"
