							<Checkbox checked={gilad} onChange={handleChange} name="gilad" />
						}
						label="Gilad Gray"
					/>
					<FormControlLabel
						control={
							<Checkbox checked={jason} onChange={handleChange} name="jason" />
						}
						label="Jason Killian"
					/>
					<FormControlLabel
						control={
							<Checkbox
								checked={antoine}
								onChange={handleChange}
								name="antoine"
							/>
