import React from "react";
import { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import FormLabel from "@material-ui/core/FormLabel";
import FormControl from "@material-ui/core/FormControl";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import Checkbox from "@material-ui/core/Checkbox";

const useStyles = makeStyles((theme) => ({
	root: {
		display: "flex",
	},
	formControl: {
		margin: theme.spacing(3),
	},
}));

const CheckBoxes = ({ area }) => {
	// const area
	const [check, setCheck] = useState(
		area.reduce((m, v) => {
			v === "All" ? (m[v] = true) : (m[v] = false);
			return m;
		}, {})
	);
	console.log("state", check);
	// for (const prop in check) {
	// 	console.log(check[prop]);
	// }
	const arrBool = Object.values(check);
	console.log(arrBool);
	console.log("props", area);
	const classes = useStyles();

	const handleChange = (event) => {
		console.log("hello");
		console.log(event);
		setCheck({ ...check, [event.target.name]: event.target.checked });
	};

	// const { All, mce } = state;
	return (
		<div className={classes.root}>
			<FormControl component="fieldset" className={classes.formControl}>
				<FormLabel component="legend">Choose Location:</FormLabel>
				<FormGroup row>
					{area.map((element, index) => (
						<FormControlLabel
							key={index}
							control={
								<Checkbox
									checked={arrBool[index]}
									onChange={handleChange}
									name={element}
								/>
							}
							label={element}
						/>
					))}
				</FormGroup>
				<FormHelperText>Be careful</FormHelperText>
			</FormControl>
		</div>
	);
};

export default CheckBoxes;
