					<FormControlLabel
						control={
							<Checkbox checked={gilad} onChange={handleChange} name="gilad" />
						}
						label="Gilad Gray"
					/>
				</FormGroup>
				<FormHelperText>
					Message From Traffic Police, Drink and Drive! YOLO
				</FormHelperText>
			</FormControl>
		</div>
	);
};

export default CheckBox;
