import React, { useEffect, useState } from "react";
import axios from "axios";
import Ticker, { NewsTicker } from "nice-react-ticker";

const GetNews = () => {
  const [news, setNews] = useState("");
  useEffect(() => {
    axios
      .get("http://localhost:4444/proxyServer/traffic_news")
      .then((res) => {
        setNews(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  useEffect(() => {
    console.log(news);
  }, [news]);

  return news ? (
    news.map((item, index) => <NewsTicker key={index} id={index} title={item.Message}/>)
  ) : (
    <NewsTicker title = "No Traffic Incident... Drive Safe!!!" />
  );
};

