import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import TrafficCard from "../components/TrafficCard";

function TrafficCam() {
	const [trafficImg, setTrafficImg] = useState("");
	const [trafficLocation, setTrafficLocation] = useState("");

	const token = process.env.REACT_APP_API_KEY_SLA_TOKEN;

	useEffect(() => {
		const camImageUrl = "https://api.data.gov.sg/v1/transport/traffic-images";

		axios
			.get(camImageUrl)
			.then((res) => {
				console.log(res.data.items[0]);
				setTrafficImg(res.data.items[0].cameras[0].image);
				const latitude = res.data.items[0].cameras[0].location.latitude;
				const longitude = res.data.items[0].cameras[0].location.longitude;
				const camLocationUrl = `https://developers.onemap.sg/privateapi/commonsvc/revgeocode?location=${latitude},${longitude}&token=${token}`;
				return axios.get(camLocationUrl);
			})
			.then((res) => {
				setTrafficLocation(res.data.GeocodeInfo[0].ROAD);
			});
	}, []);

	return (
		<div>
			<Container>
				<Grid container>
					<Grid item>
						<TrafficCard />
					</Grid>
				</Grid>
				{trafficLocation && (
					<Typography variant="h3" display="block" align="center">
						{trafficLocation} <img src={trafficImg} />
					</Typography>
				)}
			</Container>
		</div>
	);
}

export default TrafficCam;
