import React, { useEffect, useState } from "react";
import axios from "axios";
import Ticker from "react-ticker";

const GetNews = ()=>{

    const [news, setNews] = useState('');
    useEffect(() => {
      axios
        .get("http://localhost:4444/proxyServer/traffic_news")
        .then((res) => {
          setNews(res.data);
        })
        .catch((err) => {
          console.error(err);
        });
    }, []);
  
    useEffect(() => {
      console.log(news);
    }, [news]);

    return news ? (news.map(item=> (<h1>{item.Message}</h1>))) : (<h1>No Traffic Incident... Drive Safe!!!</h1>)
}

const NewsTicker = () => {
  

  return (
    <Ticker mode= "smooth">
      {() => (
        <>
          <GetNews/>
        </>
      )}
    </Ticker>
  );
};

export default NewsTicker;
